% =================================================================
% info
% =================================================================
function print_info(self,varargin)
  fprintf('Optimization stats:\n');
  if self.m_iteration_count >= self.m_max_iteration
    fprintf(...
      'iteration number reached  Max Iteration Limit = %g\n', ...
      self.m_max_iteration ...
    );
  elseif self.m_fun_evaluation_count >= self.m_max_fun_evaluation
    fprintf(...
      'function evaluations %d exceeded the maximum limit [%d]\n', ...
      self.m_fun_evaluation_count, self.m_max_fun_evaluation ...
    );
  else
    fprintf(...
      'mesh size h = %g less than tolerance = %g\n', ...
      self.m_h, self.m_tolerance ...
    );
  end
  if nargin > 1
    fun_tol = varargin{1};
    if self.m_f_best <= fun_tol
      ok_str = 'CONVERGED';
    else
      ok_str = 'NOT CONVERGED';
    end
    fprintf( ...
      [ 'Iterations/#feval: %d / %d\n' ...
        'F(x_best):         %g [%s]\n\n' ], ...
      self.m_iteration_count, ...
      self.m_fun_evaluation_count, ...
      self.m_f_best, ...
      ok_str ...
    );
  else
    fprintf( ...
      [ 'Iterations/#feval: %d / %d\n' ...
        'F(x_best):         %g\n\n' ], ...
      self.m_iteration_count, ...
      self.m_fun_evaluation_count, ...
      self.m_f_best ...
    );
  end
end
